<?php# AUTHOR: RICARDO AZZI ## CREATED: 03/10/12 #class Monsters {	private $monsters = "";	private $monsters_array = "";	private $monsters_json = "";		public function __construct() {		$this->monsters = new Xml($GLOBALS["SERVER_PATH"]."data/monster/monsters.xml");		$this->monsters_array = $this->monsters->getArray();		foreach ($this->monsters_array["monster"] as &$monster) {			$monster = $monster["@attributes"];			$monster["class"] = explode("/", $monster["file"]);			$monster["class"] = ucwords($monster["class"][0]);		}		$this->monsters_json = json_encode($this->monsters_array);	}		public function getMonsters($format = "json") {				if (strtolower($format) == "json" OR strtolower($format) == "js") {			return $this->monsters_json;		} else if (strtolower($format) == "xml") {			return $this->monsters->getXml();		} else {			return $this->monsters_array;		}	}		public function getMonster($monster_name) {	}}?>