<?php# AUTHOR: RICARDO AZZI ## CREATED: 15/11/12 #$GLOBAL["nocount"] = TRUE;require_once($_SERVER["DOCUMENT_ROOT"]."/plugins/config.php");$gender = post("gender");$site = new MySql("ttotsite");$server = new MySql("ttotserver");do {	$nome = $site->Query("SELECT txtnome FROM sugerirnome WHERE nrgender=$gender OR nrgender=2 ORDER BY rand() LIMIT 1");	$sobrenome = $site->Query("SELECT txtnome FROM sugerirnome ORDER BY rand() LIMIT 1");	$nome = $nome[0]["txtnome"]." ".$sobrenome[0]["txtnome"];	$already_in_use = $server->Query("SELECT COUNT(name) AS retorno FROM players WHERE name='$nome'");	$already_in_use = (bool) $already_in_use[0]["retorno"];} while($already_in_use);callback(array(	"name" => $nome));?>